    private void jC_Merk4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jC_Merk4ActionPerformed
        if (jC_Merk4.getSelectedItem().equals("Asics")) {                                      
            shoes.setMerk("Asics");                                                             
            shoes.setHarga(1450000);
        } else if (jC_Merk4.getSelectedItem().equals("Eagle")) {                                
            shoes.setMerk("Eagle");                                                             
            shoes.setHarga(284000);
        } else if (jC_Merk4.getSelectedItem().equals("ReinforceSpeed")) {                                 
            shoes.setMerk("ReinforceSpeed");                                                                
            shoes.setHarga(345000);
        } else if (jC_Merk4.getSelectedItem().equals("Yonex")) {                                 
            shoes.setMerk("Yonex");                                                               
            shoes.setHarga(1935000);
        } else if (jC_Merk4.getSelectedItem().equals("Victor")) {                                
            shoes.setMerk("Victor");                                                               
            shoes.setHarga(1430000);
        } else if (jC_Merk4.getSelectedItem().equals("Li Ning")) {                                
            shoes.setMerk("Li Ning");                                                              
            shoes.setHarga(1552000);
        }
        

    }//GEN-LAST:event_jC_Merk4ActionPerformed

    private void jC_Nomor4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jC_Nomor4ActionPerformed
        int harga = 0;
        if (jC_Nomor4.getSelectedItem().equals("39")) {                                      
            shoes.setNomor("39");
            shoes.setHarganomor(0);
            harga = shoes.getHarga() + shoes.getHarganomor();
        } else if (jC_Nomor4.getSelectedItem().equals("40")) {                               
            shoes.setNomor("40");
            shoes.setHarganomor(5000);
            harga = shoes.getHarga() + shoes.getHarganomor();
        } else if (jC_Nomor4.getSelectedItem().equals("41")) {                                
            shoes.setNomor("41");
            shoes.setHarganomor(10000);
            harga = shoes.getHarga() + shoes.getHarganomor();
        } else if (jC_Nomor4.getSelectedItem().equals("42")) {                              
            shoes.setNomor("42");
            shoes.setHarganomor(15000);
            harga = shoes.getHarga() + shoes.getHarganomor();
        } else if (jC_Nomor4.getSelectedItem().equals("43")) {                                
            shoes.setNomor("43");
            shoes.setHarganomor(20000);
            harga = shoes.getHarga() + shoes.getHarganomor();
        } 
  
        jT_Harga4.setText("" + harga); 
      
    }//GEN-LAST:event_jC_Nomor4ActionPerformed

    private void jT_Jumlah4KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jT_Jumlah4KeyReleased
        int jml = Integer.parseInt(jT_Jumlah4.getText());                
        shoes.setJumlah(jml);                                                                                   //Menyimpan nilai pada textfield jumlah ke variabel jumlah
        shoes.setTotalHarga(shoes.getHarga(), shoes.getHarganomor(), shoes.getJumlah(), shoes.getOngkir());
        jT_Total4.setText("" + shoes.getTotalHarga());        
    }//GEN-LAST:event_jT_Jumlah4KeyReleased

    private void jB_Beli4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jB_Beli4ActionPerformed
        shoes.setIdBarang(jT_IDbarang4.getText());
        shoes.insert();
    }//GEN-LAST:event_jB_Beli4ActionPerformed

    private void jB_Ubah4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jB_Ubah4ActionPerformed
        shoes.setIdBarang(jT_IDbarang4.getText());
        shoes.update();
    }//GEN-LAST:event_jB_Ubah4ActionPerformed

    private void jB_Batal4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jB_Batal4ActionPerformed
        shoes.setIdBarang(jT_IDbarang4.getText());
        shoes.delete();
    }//GEN-LAST:event_jB_Batal4ActionPerformed

