    private void jC_Pembayaran4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jC_Pembayaran4ActionPerformed
       if (jC_Pembayaran4.getSelectedItem().equals("Transfer")) {
            shoes.setPembayaran("Transfer");
            shoes.setStatusPengiriman("Belum Dikirim");
        } else if (jC_Pembayaran4.getSelectedItem().equals("COD")) {
            shoes.setPembayaran("COD");
            shoes.setStatusPengiriman("Dikirim 2 Hari Lagi");
        } else if (jC_Pembayaran4.getSelectedItem().equals("Indomaret")) {
            shoes.setPembayaran("Indomaret");
            shoes.setStatusPengiriman("Belum Dikirim");
        } else if (jC_Pembayaran4.getSelectedItem().equals("OVO")) {
            shoes.setPembayaran("OVO");
            shoes.setStatusPengiriman("Belum Dikirim");
        }
    }//GEN-LAST:event_jC_Pembayaran4ActionPerformed

    private void jC_WarnaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jC_WarnaActionPerformed
       if (jC_Warna.getSelectedItem().equals("Merah")) {
            shoes.setWarna("Merah");
        } else if (jC_Warna.getSelectedItem().equals("Kuning")) {
            shoes.setWarna("Kuning");
        } else if (jC_Warna.getSelectedItem().equals("Biru")) {
            shoes.setWarna("Biru");
        } else if (jC_Warna.getSelectedItem().equals("Orange")) {
            shoes.setWarna("Orange");
        }
    }//GEN-LAST:event_jC_WarnaActionPerformed

    private void jC_JasaKirim4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jC_JasaKirim4ActionPerformed
        if (jC_JasaKirim4.getSelectedItem().equals("J&T Express")) {
            shoes.setJasaKirim("J&T Express");
            shoes.setOngkir(19000);
        } else if (jC_JasaKirim4.getSelectedItem().equals("JNE")) {
            shoes.setJasaKirim("JNE");
            shoes.setOngkir(18000);
        } else if (jC_JasaKirim4.getSelectedItem().equals("Tiki")) {
            shoes.setJasaKirim("Tiki");
            shoes.setOngkir(15000);
        } else if (jC_JasaKirim4.getSelectedItem().equals("Wahana Express")) {
            shoes.setJasaKirim("Wahana Express");
            shoes.setOngkir(16000);
        } else if (jC_JasaKirim4.getSelectedItem().equals("Ninja Express")) {
            shoes.setJasaKirim("Ninja Express");
            shoes.setOngkir(12000);
        }

        jT_Ongkir4.setText("" + shoes.getOngkir());
    }//GEN-LAST:event_jC_JasaKirim4ActionPerformed

